NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;

return "" unless($NODE->hasAccess($USER, "w"));

my $PARAMS = { op => 'addBucket', "bnode_" . $$NODE{node_id} => 1 };
$this->link_node($NODE, "Add '$$NODE{title}'", $PARAMS);

%][%

my $query = $this->get_query;


$query->start_form("POST", $ENV{script_name}) .
$query->hidden("displaytype") .
$query->hidden("node_id", $$NODE{node_id}) .
$query->hidden(-name => "op", -default => "bucketOp", -override => 1);

%][%
my $query = $this->get_query;
my $NODE = $this->get_requested_node;
my $VARS = $this->get_vars;

my @bnodes;
@bnodes = split ',', $$VARS{nodebucket} if (defined($$VARS{nodebucket}));
my $isLoc = $NODE->isOfType("location");
my $isGroup = $NODE->isGroup();
my $str;

return "Empty<br>\n" unless(@bnodes);

foreach $_ (@bnodes)
{
  my $node = $DB->getNode( $_ );

  # Can't use CGI::checkbox here because it insists on having a label...
  $str .= "<INPUT TYPE='checkbox' NAME='bnode_$_' " .
     "VALUE='ON' UNCHECKED>";
  $str .= $this->link_node($node) . "<br>\n";
}

$str .= "<font size=1>\n";

$str .= "<INPUT TYPE='checkbox' NAME='dropexec' VALUE='ON' CHECKED>" .
  "Execute and drop<br>\n" if($isLoc or $isGroup);

if($isLoc)
{
  $str .= $query->submit( -name => "bmove", -value => "Move" ) . "\n";
  $str .= $query->submit( -name => "bsymlink", -value => "Symlink" ) . "\n";
}
elsif($isGroup)
{
  $str .= $query->submit( -name => "bgroupadd", -value => "Add to Group") ."\n";
}

$str .= $query->submit( -name => "bdrop", -value => "Drop" ) . "\n";
$str .= $query->submit( -name => "bflush", -value => "Drop All" ) . "\n";

$str .= "</font>\n";

$str .= $query->end_form;

$str;

%]
