NODE.nlcode:
Your fellow noders:<br>
[%

my $time = 3 * 60;
my $limit = 30;

my $csr=$Everything::dbh->prepare("	
	SELECT node_id, title FROM node, user
	WHERE node_id=user_id and
	type_nodetype=". getId ($NODETYPES{user}) ." and
	(UNIX_TIMESTAMP(now())-UNIX_TIMESTAMP(lasttime) < $time)
	LIMIT $limit");

$csr->execute or return "SHIT";
my $str;
while(my $U = $csr->fetchrow_hashref) {
	$str .= linkNode($U) ."<br>";
}
$csr->finish;
return $str if $str;
"<i>no users logged in</i>";
%]
