NODE.field:
[%
  return if ($$USER{node_id} == $HTMLVARS{guest_user});
  my $limit = $HTMLVARS{personalNodeletLimit} || 10;

  my @nodes;
  @nodes = split("<br>",$$VARS{personal_nodelet}) if ($$VARS{personal_nodelet});
  if (my $n = $query->param("addpersonalnodelet")) {
    $n = getNodeById $n;
    $$VARS{personal_nodelet} .= '<br>'.$$n{title}
      if @nodes < $limit;
    push @nodes, $$n{title};
  }


  my $str;
  my $i=0;
  foreach (@nodes) {
    $str.=linkNodeTitle($_)."<br>\n";
    last if $i++ >= 10;
  }

  $str.="\n<p align=right>"
    .linkNode($NODE, "add \"$$NODE{title}\"", {addpersonalnodelet => getId($NODE)})
    if @nodes < $limit;
  $str;

%]
