NODE.field:
my @params = $query->param;
my $old;
my $mini;
my @nodelets;
my $SETTINGS;

$old = getNode($query->param('nodelet'));
return 1 unless($old);
$mini = getNode($$old{mini_nodelet});
return 1 unless($mini);

$SETTINGS = $USER->getVars();

if ($$SETTINGS{nodelets})
{
  @nodelets = split",",$$SETTINGS{nodelets}; 
}
else
{
  my $NODELETGROUP = getNode($$SETTINGS{nodelet_group}) if exists $$SETTINGS{nodelet_group};

  if ($NODELETGROUP and $$NODELETGROUP{type}{title} eq 'nodeletgroup')
  {        
    push @nodelets, @{ $$NODELETGROUP{group} } unless @nodelets;
  }

  if (not @nodelets)
  {  
    #push default nodelets on
    my $DEFAULT = $DB->getNode($HTMLVARS{default_nodeletgroup});
    @nodelets = @{ $$DEFAULT{group} }; 
  }
}

foreach (@nodelets)
{ 
  next unless($_ eq $$old{node_id});
  $_ = $$mini{node_id};
}

$$SETTINGS{nodelets} = join",",@nodelets ;

$USER->setVars($SETTINGS);
$USER->update($USER);
return 1;
