NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;
my %GLOBAL;


 my $str="";
 my $arr = getNodeWhere({}, getType('setting'));
 my @arrsort = sort {$$a{title} cmp $$b{title}} @$arr;
 foreach (@arrsort)
  { 
    $str.= linkNode($_)."<br>";
  } 
 if (getNode('hintlaunch settings', 'restricted_superdoc'))
 {
  $str.=linkNode(getNode('hintlaunch settings', 'restricted_superdoc'));
 }
$str;

%][typeversion controls]
<p>
<hr width=90% align=center>
<h3>Theme Nodes:</h3>
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;
my %GLOBAL;


  my $str;
  my $themes = getNodeWhere({type_nodetype=>[getType('theme'), getType('themesetting')]});
  my @themesort = sort {$$a{title} cmp $$b{title}} @$themes;

  foreach (@themesort){
    $str.=linkNode($_)."<br>";
  }
  $str;

%]
<p>
[{openform}]

