NODE.field:
[%
my $str;
my $execstr = $query->param("sqlquery");

$str = $query->start_multipart_form("POST",
  $ENV{script_name}) . "\n";
$str .= $query->hidden("displaytype") . "\n";
$str .= $query->hidden("node_id", getId($NODE)) . "\n";
$str .= "SQL Query:\n";
$str .= $query->textfield("sqlquery", $execstr, 60);
$str .= $query->submit('execsql', 'Execute') . "\n";
$str .= $query->end_form;

$str;
%]

<p>
[%
my $execstr = $query->param("sqlquery");
my $str = "";

if($execstr)
{
  my $cursor = $DB->getDatabaseHandle()->prepare($execstr);

  if($cursor->execute())
  {
    my $ROW;
    while($ROW = $cursor->fetchrow_hashref())
    {
      if($str eq "")
      {
        $str = "<table border=1>\n";
        $str .= " <tr>\n";
        foreach (keys %$ROW)
        {
          $_ = "&nbsp;" if((not defined $_) || ($_ eq "")); 
          $str .= "  <td align=\"center\" bgcolor=\"#CC99CC\">$_</td>\n";
        }
        $str .= " </tr>\n";
      }

      $str .= " <tr>\n";
      my ($k, $v);
      foreach (keys %$ROW)
      {
        $k = $_;  $v = $$ROW{$_};
        $v = encodeHTML($v, 1) if(defined $v);
        $v = linkNode($v, $v) if(($k =~ /_/) && (not ($v =~ /\D/)));
        $v = "&nbsp;" if((not defined $v) || ($v eq ""));
        $str .= "  <td><pre>$v</pre></td>\n";
      }
      $str .= " </tr>\n";
    }
	$cursor->finish();
    $str .= "</table>\n";
  }
}

$str;
%]
