NODE.field:
[%
my $str;
my $execstr = $query->param("sqlquery");

$str = $query->start_form("POST",$ENV{script_name}) . "\n";
$str .= $query->hidden("displaytype") . "\n";
$str .= $query->hidden("node_id", getId($NODE)) . "\n";
$str .= "SQL Query:\n";
$str .= $query->textfield("sqlquery", $execstr, 60);
$str .= $query->submit('execsql', 'Execute') . "\n";
$str .= $query->end_form;

$str;
%]

<p>
[%
#Cleaner output of SQL Errors
local $SIG{__WARN__} = sub { return };

my $execstr = $query->param("sqlquery");
my $str     = "";

if($execstr)
{
  my $dbh    = $DB->getDatabaseHandle();
  my $cursor = eval { $dbh->prepare($execstr) };
  return 'Bad SQL: ' . $dbh->errstr . "($@)\n" if $@;

  if($cursor->execute())
  {
    my $ROW;
    my $hdr = "<table border=1>\n";

    while($ROW = $cursor->fetchrow_hashref())
    {

      if($str eq "")
      { 
        $hdr .= " <tr>\n";
        foreach (keys %$ROW)
        {
          $_ = "&nbsp;" if((not defined $_) || ($_ eq "")); 
          $hdr .= "  <td align=\"center\" bgcolor=\"#CC99CC\">$_</td>\n";
        }
        $hdr .= " </tr>\n";
      }

      $str .= " <tr>\n";
      my ($k, $v);
      foreach (keys %$ROW)
      {
        $k = $_;  $v = $$ROW{$_};
        $v = encodeHTML($v, 1) if(defined $v);
        $v = linkNode($v, $v) if(($k =~ /_/) && (not ($v =~ /\D/)));
        $v = "&nbsp;" if((not defined $v) || ($v eq ""));
        $str .= "  <td><pre>$v</pre></td>\n";
      }
      $str .= " </tr>\n";
    }
        $cursor->finish();
        $str ||= "<tr><td><em>No results found</em></td></tr>";
        $str = $hdr.$str."</table>\n";
  }
  else
  {
    return $dbh->errstr;
  }
}

$str;
%]
