$str.= $query->start_form("POST", $ENV{script_name}) .
"<INPUT TYPE=hidden NAME=node_id VALUE=$$GNL{node_id}>";

foreach (@param) {
  next unless /bnode_(\d+)/;
  my $N = getNodeById($1);

  $str.="<input type=hidden name=$_ value=1>";
  $str.="<li>".linkNode($N)." by ".linkNode($$N{author_user});

}

$str.="<p align=right>".$query->submit("op","massacre");
$str.="<p>".linkNode($GNL, "Changed my mind.");
$str.=$query->end_form;
%]

</ol>
