NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;
my %GLOBAL;



return unless $query->param('op', 'nodelistcheck');
my (@param) = $query->param;
my $str ="Are you sure you want to delete the following nodes?
<p>
<ol>";
my $GNL = getNode('gigantic node lister', 'restricted_superdoc');

$str.= $query->start_form("POST", $ENV{script_name}) .
"<INPUT TYPE=hidden NAME=node_id VALUE=$$GNL{node_id}>";

foreach (@param) {
  next unless /bnode_(\d+)/;
  my $N = getNodeById($1);

  $str.="<input type=hidden name=$_ value=1>";
  $str.="<li>".linkNode($N)." by ".linkNode($$N{author_user});

}

$str.="<p align=right>".$query->submit("op","massacre");
$str.="<p>".linkNode($GNL, "Changed my mind.");
$str.=$query->end_form;

%]

</ol>
