NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;


my $str;
my $title = $query->param('node');
return "" unless $title;
$str.="<table border=0 cellpadding=2>";

my $nodes = $this->get_request->get_message;
unless ( ref $nodes && ref $nodes eq 'ARRAY' ) {
$nodes = [];
}

foreach (@{ $nodes }) {
  my $ND = getNode $$_{node_id};
  $str .="<tr>";
  $str .= "<td>" . linkNode ($ND) . "</td>";
  $str .= "<td>(".$$ND{type}{title}.")</td>";
  my $keys = $ND->getIdentifyingFields();
  $keys ||= [];
  foreach (@$keys) {
    $str.="<td>$_: ";
    $str.= Everything::XML::getFieldType($_) eq 'noderef' 
        ? linkNode($$ND{$_}) : $$ND{$_};
    $str.="</td>";    
  }
  $str.="</tr>";
}

$str.="</table>";
$str;

%][{createnewnode}]
