NODE.field:
[%
my $str;
my $title = $query->param('node');
return "" unless $title;
$str.="<table border=0 cellpadding=2>";

foreach (@{ $GLOBAL{searchgroup} }) {
  my $ND = getNode $$_{node_id};
  $str .="<tr>";
  $str .= "<td>" . linkNode ($ND) . "</td>";
  $str .= "<td>(".$$ND{type}{title}.")</td>";
  my $keys = $ND->getIdentifyingFields();
  $keys ||= [];
  foreach (@$keys) {
    $str.="<td>$_: ";
    $str.= Everything::XML::getFieldType($_) eq 'noderef' 
        ? linkNode($$ND{$_}) : $$ND{$_};
    $str.="</td>";    
  }
  $str.="</tr>";
}

$str.="</table>";
$str;
%]
[{createnewnode}]
