NODE.field:
[%
my $NODE = $this->get_node;
my $USER = $this->get_user;
my $query = $this->get_query;
my $THEME = $this->get_theme;
my %HTMLVARS = %{ $this->get_htmlvars };
my $VARS = $this->get_vars;
my $DB = $this->get_nodebase;
my %GLOBAL;


my $str = "";

if ($query->param('op') eq 'login' && $$USER{node_id} != $HTMLVARS{guest_user}) {
  $str.= "<p>Hey.  Glad you're back.  Would you like to go to your " 
    .$this->link_node($USER, "home node")
    ." or to ".$this->link_node($HTMLVARS{default_node})."?<br />";

  $str.="...or back to "
    .$this->link_node($query->param("lastnode_id"))
    ."?<br /></p>" if ($query->param("lastnode_id"));
  return $str;
} elsif ($query->param('op') eq 'login') { 
  $str .="<p>Oops.  You must have the wrong login or password or something:\n< /p>\n";
} elsif ($$USER{node_id} != $HTMLVARS{guest_user}) {
  $str.="<p>Hey, ". $this->link_node($USER)."...  this is where you log in:</p>\n";
}else {
  $str .="<p>Welcome to $HTMLVARS{site_name}.  Authenticate yourself:\n</p>\n";
}

$str .= $query->start_form("POST", $ENV{SCRIPT_NAME}) .
                q{<fieldset class="loginnode">} .
		"<input type=\"hidden\" name=\"op\" value=\"login\" />".	
 		$query->hidden("node_id", $NODE->get_node_id)."\n".
		$query->hidden("lastnode_id", $query->param("lastnode_id"))."\n".

                q{<label for="loginnodeuser">Username:</label>}.
		$query->textfield (-name => "user",
			-size => 20,
                        -id => "loginnodeuser",
			-maxlength => 20) . "<br />" .

                q{<label for="loginnodepass">Password:</label>}.
		$query->password_field(-name => "passwd",
			-size => 10,
                        -id => 'loginnodepass',
			-maxlength => 10) ."<br />".
		$query->checkbox("expires", "", "+10y", "save me a permanent cookie, cowboy!"). 
		$query->submit("submit", "submit") .
                q{</fieldset>} .
		$query->end_form;
	$str;

%]
