NODE.code:
use Everything::FormMenu;

my ($setting, $field) = @_;
my $settings = $DB->getNode($setting, 'setting') or return;
my $vars = $settings->getVars() or return;

my @vars = split(/\s*,\s*/, $vars->{$field});
my %settings;
@settings{@vars} = @vars;

my $query_field = $query->param($field);
if ((exists $settings{$query_field}) and ($NODE->{$field} ne $query_field)) {
	$NODE->{$field} = $query_field;
	$NODE->{"update_$NODE"} = 1;
}

my $selected;

if (!defined($NODE->{$field}) || (!exists $settings{$NODE->{$field}})) {
	$NODE->{$field} = $selected = $vars[0];
	$NODE->{"update_$NODE"} = 1;
} else {
	$selected = $NODE->{$field};
}

my $menu = Everything::FormMenu->new();
$menu->addHash( \%settings );
return $menu->writePopupHTML($query, $field, $selected);
