NODE.code:
my ($this, $USER) = @_;

# default time is a week from now
my @times = (localtime(time + 604800))[3 .. 5];
$times[1] += 1;
$times[2] += 1900;
$this->{due_date} = join("-", reverse @times);

# default task owner is task author
$this->{owner} = $USER->getId();

my $task_loc = $this->{DB}->getNodeCursor('node_id',
	{ title => 'tasks' }, 'location', '', '', '', 1);

if (defined $task_loc) {
	($task_loc) = $task_loc->fetchrow_array();
}
$task_loc ||= 0;

$this->{loc_location} = $task_loc;

$this->SUPER($USER);
